import pandas as pdfrom pandas import DataFramefrom sqlalchemy import create_engineclass LoadToDestination:    def __init__(self,                 username: str,                 password: str,                 host: str,                 port: str,                 dbname: str):        self._engine = create_engine(f"postgresql+psycopg2://{username}:{password}@{host}:{port}/{dbname}")        self._conn = self._engine.connect()    def get_load_data(self, query: str) -> DataFrame:        df = pd.read_sql(query, self._conn)        return df    def save_to_destination(self, df: DataFrame, table_name: str) -> None:        df.to_sql(table_name,                  self._conn,                  index=False,                  if_exists='replace',                  chunksize=1000)    def close(self) -> None:        if self._conn is not None:            self._conn.close()